INSERT INTO `oc_modification` VALUES (28,'<font color=\"#0000\"><b>Add new position</font>','loadposition','<font color=\"#CC0000\"><b>Bossthemes</font>','<b>1.0</b>','www.bossthemes.com','<modification>\r\n   <name><![CDATA[<font color=\"#0000\"><b>Add new position</font>]]></name>\r\n    <version><![CDATA[<b>1.0</b>]]></version>\r\n	<code>loadposition</code>\r\n    <author><![CDATA[<font color=\"#CC0000\"><b>Bossthemes</font>]]></author>\r\n    <link><![CDATA[www.bossthemes.com]]></link>\r\n<!-- sw-ad@hotmail.com -->	                      \r\n<file path=\"catalog/controller/common/header.php\">\r\n		<operation>\r\n			<search><![CDATA[\r\n			$data[\'links\'] = $this->document->getLinks();\r\n			]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			$data[\'btheader\'] = $this->load->controller(\'bossthemes/btheader\');\r\n			$data[\'btslideshow\'] = $this->load->controller(\'bossthemes/btslideshow\');\r\n			$data[\'btmainmenu\'] = $this->load->controller(\'bossthemes/btmainmenu\');\r\n			]]></add>\r\n		</operation>\r\n</file>\r\n<file path=\"catalog/controller/common/footer.php\">\r\n		<operation>\r\n			<search><![CDATA[\r\n			$data[\'newsletter\'] = $this->url->link(\'account/newsletter\', \'\', \'SSL\');\r\n			]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			$data[\'btfooter\'] = $this->load->controller(\'bossthemes/btfooter\');\r\n			]]></add>\r\n		</operation>\r\n</file>\r\n<file path=\"admin/view/template/design/layout_form.tpl\">\r\n		<operation>\r\n			<search><![CDATA[\r\n			<td class=\"text-left\"><select name=\"layout_module[<?php echo $module_row; ?>][position]\" class=\"form-control\">\r\n			]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n            <option value=\"btheader\" <?php echo ($layout_module[\'position\'] == \'btheader\')?\'selected=\"selected\"\':\'\'; ?>><?php echo \'Header\'; ?></option>\r\n			<option value=\"btmainmenu\" <?php echo ($layout_module[\'position\'] == \'btmainmenu\')?\'selected=\"selected\"\':\'\'; ?>><?php echo \'Main Menu\'; ?></option>\r\n			<option value=\"btslideshow\" <?php echo ($layout_module[\'position\'] == \'btslideshow\')?\'selected=\"selected\"\':\'\'; ?>><?php echo \'Slideshow\'; ?></option>\r\n			<option value=\"btfooter\" <?php echo ($layout_module[\'position\'] == \'btfooter\')?\'selected=\"selected\"\':\'\'; ?>><?php echo \'Footer\'; ?></option>\r\n			]]></add>\r\n		</operation>\r\n		<operation>\r\n			<search><![CDATA[\r\n			html += \'    <option value=\"content_top\"><?php echo $text_content_top; ?></option>\';\r\n			]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n            html += \'    <option value=\"btheader\"><?php echo \'Header\'; ?></option>\';\r\n			html += \'    <option value=\"btmainmenu\"><?php echo \'Main Menu\'; ?></option>\';\r\n			html += \'    <option value=\"btslideshow\"><?php echo \'Slideshow\'; ?></option>\';\r\n			html += \'    <option value=\"btfooter\"><?php echo \'Footer\'; ?></option>\';\r\n			]]></add>\r\n		</operation>\r\n		\r\n</file>\r\n</modification>					   \r\n					   ',1,'2014-12-09 10:26:57'),(30,'<font color=\"#0000\"><b>Add Newsletter Menu</font>','newsletter','<font color=\"#CC0000\"><b>Bossthemes</font>','<b>1.0</b>','www.bossthemes.com','<modification>\r\n   <name><![CDATA[<font color=\"#0000\"><b>Add Newsletter Menu</font>]]></name>\r\n    <version><![CDATA[<b>1.0</b>]]></version>\r\n    <author><![CDATA[<font color=\"#CC0000\"><b>Bossthemes</font>]]></author>\r\n    <link><![CDATA[www.bossthemes.com]]></link>\r\n	<code>newsletter</code>\r\n	<file path=\"admin/controller/common/menu.php\">\r\n		<operation>\r\n			<search><![CDATA[\r\n            $this->load->language(\'common/menu\');\r\n            ]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n            $data[\'text_newssubscribe\'] = $this->language->get(\'text_newssubscribe\');\r\n			$data[\'newssubscribe\'] = $this->url->link(\'sale/newssubscribers\', \'token=\' . $this->session->data[\'token\'], \'SSL\');\r\n            ]]></add>\r\n		</operation>\r\n	</file>\r\n    	<file path=\"admin/language/*/common/menu.php\">\r\n		<operation>\r\n			<search><![CDATA[$_[\'text_system\']                      = \'System\';]]></search>\r\n			<add position=\"after\"><![CDATA[$_[\'text_newssubscribe\']     = \'Newsletter Subscribers\';]]></add>\r\n		</operation>\r\n	</file>\r\n    	<file path=\"admin/controller/marketing/contact.php\">\r\n		<operation>\r\n			<search><![CDATA[\r\n            case \'newsletter\':\r\n            ]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n            $results = $this->model_sale_customer->getNewsletterSubscribers();\r\n\r\n						foreach ($results as $result) {\r\n							$emails[\"s\".$result[\'id\']] = $result[\'email_id\'];\r\n						}\r\n            ]]></add>\r\n		</operation>\r\n	</file>\r\n    <file path=\"admin/model/sale/customer.php\">\r\n		<operation>\r\n			<search><![CDATA[\r\n            class ModelSaleCustomer extends Model {\r\n            ]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n            public function getNewsletterSubscribers() {\r\n		$query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"subscribe\");\r\n\r\n		return $query->rows;\r\n	}\r\n            ]]></add>\r\n		</operation>\r\n	</file>\r\n    <file path=\"admin/view/template/common/menu.tpl\">\r\n		<operation>\r\n			<search><![CDATA[\r\n            <li><a href=\"<?php echo $contact; ?>\"><?php echo $text_contact; ?></a></li>\r\n            ]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n            <li><a href=\"<?php echo $newssubscribe; ?>\"><?php echo $text_newssubscribe; ?></a></li>\r\n            ]]></add>\r\n		</operation>\r\n	</file>\r\n</modification>',1,'2014-12-09 10:27:04'),(34,'<font color=\"#0000\"><b>Seo Boss Blog</font>','13','<font color=\"#CC0000\"><b>Bossthemes</font>','<b>1.0</b>','www.bossthemes.com','<modification>\r\n   <name><![CDATA[<font color=\"#0000\"><b>Seo Boss Blog</font>]]></name>\r\n    <version><![CDATA[<b>1.0</b>]]></version>\r\n    <author><![CDATA[<font color=\"#CC0000\"><b>Bossthemes</font>]]></author>\r\n    <link><![CDATA[www.bossthemes.com]]></link>\r\n	<code>13</code>\r\n<!-- sw-ad@hotmail.com -->	                      \r\n<file path=\"catalog/controller/common/seo_url.php\">\r\n		<operation>\r\n			<search><![CDATA[\r\n			if ($query->row[\'query\'] && $url[0] != \'information_id\' && $url[0] != \'manufacturer_id\' && $url[0] != \'category_id\' && $url[0] != \'product_id\') {\r\n			]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n			if ($url[0] == \'blog_article_id\') {\r\n				$this->request->get[\'blog_article_id\'] = $url[1];\r\n			}\r\n			\r\n			if ($url[0] == \'blog_category_id\') {\r\n				$blog = 1;\r\n				if (!isset($this->request->get[\'path\'])) {\r\n					$this->request->get[\'path\'] = $url[1];\r\n				} else {\r\n					$this->request->get[\'path\'] .= \'_\' . $url[1];\r\n				}\r\n			}\r\n			]]></add>\r\n		</operation>\r\n		<operation>\r\n			<search><![CDATA[\r\n			if ($query->row[\'query\'] && $url[0] != \'information_id\' && $url[0] != \'manufacturer_id\' && $url[0] != \'category_id\' && $url[0] != \'product_id\') {\r\n			]]></search>\r\n			<add position=\"replace\"><![CDATA[\r\n			if ($query->row[\'query\'] && $url[0] != \'information_id\' && $url[0] != \'manufacturer_id\' && $url[0] != \'category_id\' && $url[0] != \'product_id\' && $url[0] != \'blog_category_id\' && $url[0] != \'blog_article_id\') {\r\n			]]></add>\r\n		</operation>\r\n		<operation>\r\n			<search><![CDATA[\r\n			} elseif (isset($this->request->get[\'path\'])) {\r\n			]]></search>\r\n			<add position=\"replace\"><![CDATA[\r\n			} elseif (isset($this->request->get[\'path\']) && !isset($blog)) {\r\n			]]></add>\r\n		</operation>\r\n		<operation>\r\n			<search><![CDATA[\r\n			$this->request->get[\'route\'] = \'information/information\';\r\n			]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			}elseif (isset($this->request->get[\'blog_article_id\'])) {\r\n					$this->request->get[\'route\'] = \'bossblog/article\';\r\n				}\r\n			elseif (isset($this->request->get[\'path\']) && isset($blog)) {\r\n					$this->request->get[\'route\'] = \'bossblog/blogcategory\';\r\n			]]></add>\r\n		</operation>\r\n		<operation>\r\n			<search><![CDATA[\r\n			if (($data[\'route\'] == \'product/product\' && $key == \'product_id\') || (($data[\'route\'] == \'product/manufacturer/info\' || $data[\'route\'] == \'product/product\') && $key == \'manufacturer_id\') || ($data[\'route\'] == \'information/information\' && $key == \'information_id\')) {\r\n			]]></search>\r\n			<add position=\"replace\"><![CDATA[\r\n			if (($data[\'route\'] == \'product/product\' && $key == \'product_id\') || (($data[\'route\'] == \'product/manufacturer/info\' || $data[\'route\'] == \'product/product\') && $key == \'manufacturer_id\') || ($data[\'route\'] == \'information/information\' && $key == \'information_id\') || ($data[\'route\'] == \'bossblog/article\' && $key == \'blog_article_id\') || ($data[\'route\'] == \'bossblog/bossblog\'&& $value=\'bossblog/bossblog\')  || ($data[\'route\'] == \'bossblog/blogsearch\'&& $value=\'bossblog/blogsearch\')) {\r\n			]]></add>\r\n		</operation>\r\n		<operation>\r\n			<search><![CDATA[\r\n			$query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"url_alias WHERE `query` = \'category_id=\" . (int)$category . \"\'\");\r\n			]]></search>\r\n			<add position=\"replace\"><![CDATA[\r\n			$query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"url_alias WHERE `query` = \'blog_category_id=\" . (int)$category . \"\'\");\r\n			]]></add>\r\n		</operation>\r\n		<operation>\r\n			<search><![CDATA[\r\n			} elseif ($key == \'path\') {\r\n			]]></search>\r\n			<add position=\"replace\"><![CDATA[\r\n			} elseif ($key == \'path\' && $data[\'route\'] == \'product/category\') {\r\n				$categories = explode(\'_\', $value);\r\n\r\n					foreach ($categories as $category) {\r\n						$query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"url_alias WHERE `query` = \'category_id=\" . (int)$category . \"\'\");\r\n\r\n						if ($query->num_rows && $query->row[\'keyword\']) {\r\n							$url .= \'/\' . $query->row[\'keyword\'];\r\n						} else {\r\n							$url = \'\';\r\n\r\n							break;\r\n						}\r\n					}\r\n\r\n					unset($data[$key]);\r\n				}elseif ($key == \'path\' && $data[\'route\'] == \'bossblog/blogcategory\') {\r\n			]]></add>\r\n		</operation>		\r\n		\r\n</file>\r\n</modification>					   \r\n					   ',1,'2015-01-19 22:39:23'),(35,'Product Recently Viewed','product_recently_viewed','bossthemes.com','2.0.x','http://www.bossthemes.com','<modification>\r\n   	<name>Product Recently Viewed</name>\r\n	<code>product_recently_viewed</code>\r\n    <version>2.0.x</version>\r\n    <author>bossthemes.com</author>\r\n    <link>http://www.bossthemes.com</link>\r\n<file path=\"catalog/controller/common/footer.php\">\r\n		<operation>\r\n			<search position=\"replace\"><![CDATA[$this->load->language(\'common/footer\');]]></search>\r\n			<add><![CDATA[$this->load->language(\'common/footer\');\r\n		if (isset($this->request->get[\'product_id\'])) {\r\n			$this->session->data[\'recently_viewed\'][]= (int)$this->request->get[\'product_id\'];\r\n		}]]></add>\r\n		</operation>	\r\n</file>\r\n</modification>',1,'2015-05-21 11:13:54');
